# for at lave hexagonalt grid, er alle rækker forskudt således:

# 1:       -1  0  1  2
# 0:     -1  0  1  2
# -1:      -1  0  1  2
# -2:   -2  -1  0  1  2

x = 0
y = 0

fliptiles = data.split(";")
flipped = {}

for i in range(len(fliptiles)):
    fliptiles[i] = list(fliptiles[i])
    index = 0
    while(index < len(fliptiles[i])):
        if(fliptiles[i][index] == "e"):
            x += 1
            index += 1
        elif(fliptiles[i][index] == "w"):
            x -= 1
            index += 1
        elif(fliptiles[i][index] == "s" and fliptiles[i][index+1] == "e"):
            if(y > 0):
                x += 1
            y -= 1
            index += 2
        elif(fliptiles[i][index] == "s" and fliptiles[i][index+1] == "w"):
            if(y <= 0):
                x -= 1
            y -= 1
            index += 2
        elif(fliptiles[i][index] == "n" and fliptiles[i][index+1] == "e"):
            if(y < 0):
                x += 1
            y += 1
            index += 2
        elif(fliptiles[i][index] == "n" and fliptiles[i][index+1] == "w"):
            if(y >= 0):
                x -= 1
            y += 1
            index += 2
        else:
            print("Error whilst reading input...")
    loc = ",".join([str(x),str(y)])
    if(loc in flipped):
        flipped[loc] = (flipped[loc]+1) % 2
    else:
        flipped[loc] = 1
    x = 0
    y = 0

# saa begynder del b:

def game_of_life_hex():
    global flipped
    nearby = {}
    listflipped = list(flipped)
    # resetting nearby at known locations before inserting
    for i in range(len(listflipped)):
        nearby[listflipped[i]] = 0
    for i in range(len(listflipped)):
        if(flipped[listflipped[i]] == 1):
            # alle sorte
            tmp = listflipped[i].split(",")
            xloc = int(tmp[0])
            yloc = int(tmp[1])

            # finding all neighbors
            newlocy = [yloc+1,yloc+1,yloc,yloc,yloc-1,yloc-1]
            if(yloc > 0):
                newlocx = [xloc-1,xloc,xloc-1,xloc+1,xloc,xloc+1]
            elif(yloc < 0):
                newlocx = [xloc,xloc+1,xloc-1,xloc+1,xloc-1,xloc]
            elif(yloc == 0):
                newlocx = [xloc-1,xloc,xloc-1,xloc+1,xloc-1,xloc]
            # placing in neighbors
            for i in range(6):
                newloc = ",".join([str(newlocx[i]),str(newlocy[i])])
                if(newloc in nearby):
                    nearby[newloc] += 1
                else:
                    nearby[newloc] = 1

    to_be_flipped = []
    listnearby = list(nearby)
    for i in range(len(listnearby)):
        tmp = listnearby[i].split(",")
        xloc = int(tmp[0])
        yloc = int(tmp[1])
        if(listnearby[i] in flipped):
            if(flipped[listnearby[i]] == 1):
                if(nearby[listnearby[i]] == 0 or nearby[listnearby[i]] > 2):
                    to_be_flipped.append(listnearby[i])
            else:
                if(nearby[listnearby[i]] == 2):
                    to_be_flipped.append(listnearby[i])
        else:
            if(nearby[listnearby[i]] == 2):
                to_be_flipped.append(listnearby[i])

    # Flipping all after finding what to be flipped
    for i in range(len(to_be_flipped)):
        if(to_be_flipped[i] in flipped):
            flipped[to_be_flipped[i]] = (flipped[to_be_flipped[i]]+1) % 2
        else:
            flipped[to_be_flipped[i]] = 1

for i in range(101):
    sum = 0
    listflipped = list(flipped)
    for o in range(len(listflipped)):
        sum += flipped[listflipped[o]]
    print("for dag ", i, " findes ", sum, " flipped tiles!")
    game_of_life_hex()
